import React, {Component} from 'react';
import axios from 'axios';

function OrderForm(props){
	const {id} = props;
	const productID = React.useRef(null);
  const FullName = React.useRef(null);
  const phoneNumber = React.useRef(null);
  const noOfItem = React.useRef(null);
  const UserEmail = React.useRef(null);
  const handleSubmit = e => {
    e.preventDefault();

    const data = {
    	id:id,
      fullname: FullName.current.value,
      phone: phoneNumber.current.value,
      itemnum: noOfItem.current.value,
      email: UserEmail.current.value,
    }
    const url = "https://localhost/motreats/api/process.php"
    axios.post('/', { fullname, phone, itemnum,email, id})
          .then((result) => {
            //access the results here....
          });
    // Submit form details to login endpoint etc.
    // ...
  };

  return (
     <form onSubmit={handleSubmit}>
     <div className="form-group">
       		<input type="text" disabled="disabled" placeholder="Product ID" value={id} ref={productID} className="form-control"/>
      </div>
      <div className="form-group">
       		<input type="text" placeholder="Full Name" ref={FullName} className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="email" placeholder="User Email" ref={UserEmail} className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="text" placeholder="User Phone" ref={phoneNumber} className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="number" placeholder="Number of Item" ref={noOfItem} className="form-control"/>
       	</div>
       
       <button type="submit" className="myButton btn btnhover">Submit Order</button>
     </form>
   );
}
export default OrderForm;
