import React, {Component} from 'react';
import axios from 'axios';

class OrderForm extends Component{

	constructor(props) {
        super();
        const {id}= props;
        this.state = {
        id:id,
      fullname: '',
      phone: '',
      itemnum: '',
      email: '',
        };
         
      }
   onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
      }
  handleSubmit = e => {
    e.preventDefault();
    const {id,fullname,phone,itemnum,email}=this.state;
    const url = "https://localhost/motreats/api/process.php";
    axios.post(url, { id,fullname,phone,itemnum,email})
          .then((result) => {
            		alert(result.response);
          });
    // Submit form details to login endpoint etc.
    // ...
  };
  render(){
  	const {id,fullname,phone,itemnum,email}=this.state;
  		return (
  	<form onSubmit={this.handleSubmit}>
     <div className="form-group">
       		<input type="text" disabled="disabled" placeholder="Product ID" value={id}  className="form-control"/>
      </div>
      <div className="form-group">
       		<input type="text" placeholder="Full Name" className="form-control" name="fullname" onChange={this.onChange}/>
       </div>
       <div className="form-group">
       		<input type="email" placeholder="User Email" className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="text" placeholder="User Phone"  className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="number" placeholder="Number of Item"  className="form-control"/>
       	</div>
       
       <button type="submit" className="myButton btn btnhover">Submit Order</button>
     </form>

  	);
  }
}
export default OrderForm;
