import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import Header from './../Layout/Header';
import Footer from './../Layout/Footer';
import ShopproductID from './../Pages/ShopproductID';
import {Form} from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import axios from 'axios';


var img1= require('./../../images/banner/bnr1.jpg');
var product_id= <MyComponent/>;
class Shopproduct extends Component{
	fetchProduct = () => {
        this.setState({...this.state, isFetching: true});
        axios.get('https://localhost/motreats/api/products.php?id='+product_id)
            .then(response => {
                this.setState({data: response.data, isFetching: false})
            })
            .catch(e => {
                console.log(e);
                this.setState({...this.state, isFetching: false});
            });
    };
SelectedProduct = this.fetchProduct;

	render(){
		const pid= this.props.match.params.productID;	 

		if(pid==""){
			return(
				<>
					<PageHeader/>
					<ShopproductID/>
					<Footer/>
				</>
				)
		}else{
			return(
					<>
						<PageHeader/>
							<div className="content-block">
								<div className="section-full content-inner-1 bg-gray-light">
									<div className="container woo-entry">
										<div className="row">askskskskksksk
											{fetchProduct.id}
										</div>
									</div>
								</div>
							</div>
						<Footer/>
					</>

				)
		}


	}
}
 
export default Shopproduct; 
function MyComponent() {
  let { productID } = useParams();

  return (<p>{productID}</p>);
}
export {MyComponent}
function PageHeader(){
	return(
			<>
				<Header />
				<div className="page-content bg-white">
					
					<div className="dlab-bnr-inr overlay-black-middle" style={{backgroundImage:"url(" + img1 + ")"}}>
						<div className="container">
							<div className="dlab-bnr-inr-entry">
								<h1 className="text-white">Product Details</h1>
								
								<div className="breadcrumb-row">
									<ul className="list-inline">
										<li><Link to={'./'}><i className="fa fa-home"></i></Link></li>
										<li>Product Details</li>
									</ul>
								</div>
								
							</div>
						</div>
					</div>
				</div>
				
				</>


		)
}
