import React, {Component} from 'react';
import axios from 'axios';

class OrderForm extends Component{

	constructor() {
        super();
        const id= this.props.match.params.productID;
        this.state = {
        id:id,
      fullname: '',
      phone: '',
      itemnum: '',
      email: '',
        };
         
      }

  handleSubmit = e => {
    e.preventDefault();
    const url = "https://localhost/motreats/api/process.php"
    axios.post(url, { id,fullname,phone,itemnum,email})
          .then((result) => {
            		alert(result.response);
          });
    // Submit form details to login endpoint etc.
    // ...
  };
  render(){
  		return (
  	<form onSubmit={handleSubmit}>
     <div className="form-group">
       		<input type="text" disabled="disabled" placeholder="Product ID" value={id}  className="form-control"/>
      </div>
      <div className="form-group">
       		<input type="text" placeholder="Full Name" ref={FullName} className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="email" placeholder="User Email" className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="text" placeholder="User Phone" ref={phoneNumber} className="form-control"/>
       </div>
       <div className="form-group">
       		<input type="number" placeholder="Number of Item" ref={noOfItem} className="form-control"/>
       	</div>
       
       <button type="submit" className="myButton btn btnhover">Submit Order</button>
     </form>

  	);
  }
}
export default OrderForm;
