import React from 'react';


function OrderForm (props){
	const {id} = props;
	const productID = React.useRef(null);
  const FullName = React.useRef(null);
  const phoneNumber = React.useRef(null);
  const noOfItem = React.useRef(null);
  const UserEmail = React.useRef(null);
  const handleSubmit = e => {
    e.preventDefault();

    const data = {
      fullname: FullName.current.value,
      phone: phoneNumber.current.value,
      itemnum: noOfItem.current.value,
      email: UserEmail.current.value,
    }

    // Submit form details to login endpoint etc.
    // ...
  };

  return (
     <form onSubmit={handleSubmit}>
     <div className="form-group">
       <input type="text" placeholder="Product ID" value="" ref={productID} className="form-control"/>
      </div>
       <input type="text" placeholder="Full Name" ref={FullName} />
       <input type="number" placeholder="Number of Item" ref={noOfItem} />
       <input type="email" placeholder="User Email" ref={UserEmail} />

       <button type="submit" className="myButton">Login</button>
     </form>
   );
}
export default OrderForm;
